# coding=UTF-8
# ex:ts=4:sw=4:et=on

toolkit = "qt"

def load(tkreg):
    # Load main loop functions
    from .toolkit_functions import (
        add_idle_call, remove_source,
        add_timeout_call,
        start_event_loop, stop_event_loop
    )

    # Register them
    tkreg.set_toolkit_functions(
        add_idle_call, remove_source,
        add_timeout_call, remove_source,
        start_event_loop, stop_event_loop
    )
