from PySide6.QtGui import QColor, QPixmap


def convert_string_to_gdk_color_int(color):
    """
    Converts a hex color string to a packed RGBA integer.
    Replaces the GTK Gdk.color_parse version.
    """
    qcolor = QColor(color)
    return (
        (qcolor.red()   << 24) |
        (qcolor.green() << 16) |
        (qcolor.blue()  <<  8) |
        255
    )


def get_color_pb(color, width, height):
    """
    Returns a QPixmap of the given size filled with the given color string.
    Replaces the GTK GdkPixbuf version.
    """
    pm = QPixmap(width, height)
    pm.fill(QColor(color))
    return pm
